sp_rectangle_menu_item.record_script:
// maximum number of entries in this Menu
var max = 30;

var t = data;  // shortcut
t.items = [];

// use record watchers to update list
t.record_watchers = [];
t.record_watchers.push({'table':'sp_portal','filter':'u_site_nav_flag=true'});

var prtl = new GlideRecord('sp_portal');
prtl.addQuery('u_site_nav_flag', true);
prtl.orderByDesc('title');
prtl.setLimit(max);
prtl.query();
while(prtl.next()) {
  var a = {
    title: prtl.title.getDisplayValue(),
    type: 'link',
    href: '/' + prtl.url_suffix.getDisplayValue(),
    sys_id: prtl.sys_id.getDisplayValue(),
  };
  t.items.push(a);
}

t.omitBadge = true;
t.items = t.items.slice(0, max); // only want first 30
t.count = t.items.length;
