sp_widget.demo_data:
{
	"options": {
		"title": "Technical Support Services",
		"link_template": "Color Box",
		"short_description": "Technical Support Services, including assistance with desktops, laptops, printers, mobile and handheld devices.",
		"category_ref": "cca015cb4f305740f74b495d0210c7f3",
		"glyph": "anchor",
		"color": "default",
		"class_name": "",
		"sc_page": "ucsb_sc_category",
		"sc_category": "54a055cb4f305740f74b495d0210c775"
	}
}

sp_widget.script:
(function(){
	var categoryID = data.id = options.sc_category;
	var page = options.sc_page || 'ucsb_sc_category';

	data.title = options.title || options.sc_category_dv;
	data.short_description = options.short_description;
	data.fields = options;
	data.href = '?id=' + page + '&sys_id=' + categoryID;
	data.target = options.target || "";

	var categoryJS = new sn_sc.CatCategory(categoryID);
	if (!categoryJS)
		return;

	// gather related subcategories
	var subcategories = data.subcategories = [];
	var buildSubcatArr = categoryJS.getViewableSubCategories();
	for (var subcat = 0; subcat < buildSubcatArr.length; subcat++) {
		var subcategory = {};
		subcategory.title = buildSubcatArr[subcat].title;
		subcategory.description = buildSubcatArr[subcat].description;
		subcategory.sys_id = buildSubcatArr[subcat].sys_id;
		subcategory.order = buildSubcatArr[subcat].order;
		subcategory.page = 'ucsb_sc_category';

		subcategories.push(subcategory)

	}

})();
